import rootReducer  from './reducer/index';
import thunk from 'redux-thunk';
import { createStore, applyMiddleware, compose } from 'redux';
import { createWrappe } from "next-redux-wrapper";

const initStore = (initialState, options) => {
	let composerEnhancers = compose;

	if (process.browser === 'undefined') {
		composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
	}


	// const store = createStore(rootReducer, initialState, composerEnhancers(
	// 	applyMiddleware(thunk)
	// ));

	const makeStore = createStore(rootReducer, compose(applyMiddleware(...middleware)));
	const wrapper = createWrapper(makeStore);

	return wrapper;

};

export default initStore;
