import Layout from "../components/MyLayout.js";
import { postWriting } from "../lib/utilClass.js";
import { getInfo } from "../lib/utilGetClass.js";

export default class Class extends React.Component {
	constructor(props) {
		super(props);
		this.state = {paragraph: ""};
	}

	handleUpdate(evt) {
		this.setState({ paragraph: evt.target.value });
	}

	async handleAddUpdate(evt) {
		await postWriting(this.state.paragraph);
		alert("Added!");
	}

	async handleGetPosts(evt) {
		const myParagraph = await getInfo();
		console.log(myParagraph);
		this.setState({ myParagraph: myParagraph });
	}

	render() {
		return (
			<Layout>
			<div className='background' style={{height:575}}>
				<p><input type='text' value={this.state.paragraph} onChange={this.handleUpdate.bind(this)} /></p>
				<button className="button-style" onClick={this.handleAddUpdate.bind(this)}>Add</button>

				{this.state.myParagraph ? <div>
					<h4>{this.state.myParagraph.para}</h4>
				</div> : <p></p> }
			</div>
			</Layout>
		)
	}
}
