import React, {useEffect, useReducer, useRef} from 'react';
import ReactDOM from 'react-dom';

class Home extends React.Component {

	// focus on start button before timer is started
	// once its started switch focus to stop button
	// redo

	constructor(props) {
		super(props);
		this.state = {
			milliSecondsElapsed: 0,
			timerInProgress: false // state to detect when timer has started
		};
		this.updateState = this.updateState.bind(this);
		this.textInput = React.createRef();

	}

	updateState(e) {
		this.setState({milliSecondsElapsed: e.target.milliSecondsElapsed })
	}

	keyPress = (e) => {
	  // some logic to assess stop/start of timer
	  if (e.keyCode === 32) {
		  if (this.state.milliSecondsElapsed === 0) {
		    this.startBtn.click();
		  } else if (this.state.timerInProgress === false) {
		    this.startBtn.click();
		  } else {
		    this.stopBtn.click();
		  }
		}
	};

	getMilliseconds() {
		return ("0" + this.state.milliSecondsElapsed).slice(-2);
	}

	getSeconds() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		seconds = milli / 100;
		var myTrunc = Math.trunc(seconds);
		myTrunc = myTrunc % 60;
		return ("0" + myTrunc).slice(-2);
	}

	getMinutes() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		var minutes = 0;
		seconds = milli / 100;
		minutes = seconds / 60;
		var myTrunc = Math.trunc(minutes);
		return ("0" + myTrunc).slice(-2);
	}

	handleStart = () => {
		if (this.state.timerInProgress === true) {
			return;
		}
		this.setState({
			milliSecondsElapsed: (0)
		});
		this.timer = setInterval(() => {
			this.setState({
				milliSecondsElapsed: (this.state.milliSecondsElapsed + 1),
				timerInProgress: true
			},
			() => {
				this.stopBtn.focus(); // stop the timer
			});
		}, 10)
	}

	handleStop = () => {
  		this.setState(
    		{
      			timerInProgress: false
    		},
    		() => {
      		clearInterval(this.timer);
      		this.startBtn.focus(); // start the timer
    		}
  		);
	};

	componentDidMount() {
		window.addEventListener("keypress", this.keyPress);

	}

	componentWillUnmount() {
		window.addEventListener("keypress", this.keyPress);
	}

	textInput = () => {
		clearInterval(this.timer);
	}

	keyPress = (e) => {
    	// some logic to assess stop/start of timer
   		if (this.state.milliSecondsElapsed === 0) {
	        this.handleStart();
	    } else if (this.state.timerInProgress === false) {
	        this.handleStart();
	    } else {
	        this.handleStop();
	    }
	};



	// <button onClick = {this.handleStart}>START</button>
	// <button onClick = {this.handleStop}>STOP</button>
	// <input value = {this.state.milliSecondsElapsed} onChange = {this.updateState} ref={this.textInput}/>

	render() {
		return (			
			<div className="index">
			<input value={this.state.milliSecondsElapsed} onChange={this.updateState} ref={this.textInput} readOnly={true} />
				<button onClick={this.handleStart} ref={(ref) => (this.startBtn = ref)}>START</button>
				<button onClick={this.handleStop} ref={(ref) => (this.startBtn = ref)}>STOP</button>

			</div>

		);
	}

}

export default Home;
