import React from 'react';

class Home extends React.Component {
	constructor(props) {
		super(props);
		this.state = {milliSecondsElapsed: 0};
		// this.handleStartClick = this.handleStartClick.bind(this);

	}

	getMilliseconds() {
		return ("0" + this.state.milliSecondsElapsed).slice(-2);
	}

	// ('0' + this.state.milliSecondsElapsed * 1000).slice(-2);
	getSeconds() {
		return ('0' + this.state.milliSecondsElapsed / 1000).slice(1,4);
	}

	// this isn't resetting at 0 after an hour, another '0' and slice?
	getMinutes() {
		return Math.floor((this.state.milliSecondsElapsed / 1000) / 60);
	}

	// getHours() {
	// 	return Math.floor(this.state.milliSecondsElapsed);
	// }


	// using these arrow functions = () = they bind this of the parent for you
	// so don't need it in constructor
	// 1000 is one second
	// setInterval can't be trusted
	handleStartClick = () => {
		// var start = Date.now();
		this.timer = setInterval(() => {
			this.setState({
				milliSecondsElapsed: (this.state.milliSecondsElapsed + 1)
			});
		}, 10)
	}

	handleStopClick = () => {
		clearInterval(this.timer);
	}

	// <h1>{this.getMilliseconds()}</h1>
	render() {
		return (
			<div>
				<h1>{this.state.milliSecondsElapsed.toString().length}</h1>
				<h1>{this.state.milliSecondsElapsed}</h1>
				<h1>{this.getMinutes()}:{this.getSeconds()}:{this.getMilliseconds()}</h1>
				<button type="button" onClick={this.handleStartClick}> start </button>
				<button type="button" onClick={this.handleStopClick}> stop </button>
			</div>
		);
	}

}

export default Home;
