// App.js (client)
import React from 'react';
import { addTime } from "./util";

function App() {
  const [name, setName] = React.useState("")
  const [time, setTime] = React.useState("")
  const [cube, setCube] = React.useState("");
  const rx_live = /^[+-]?\d*(?:[.,]\d*)?$/;

  function handleUpdate(evt) {
    setName(evt.target.value);
  }

  function handleUpdateTime(evt) {
    if (rx_live.test(evt.target.value)) {
      setTime(evt.target.value);
    } else {
      alert("Please enter a number here.")
    }
  }

  function showDropdownMenu(event) {
    event.preventDefault();
    this.setState({ displayMenu: true }, () => {
      document.addEventListener('click', this.hideDropdownMenu);
    });
  }

  function hideDropdownMenu() {
      this.setState({ displayMenu: false }, () => {
        document.removeEventListener('click', this.hideDropdownMenu);
      });
  }

  // declare is false in case the user has selected both
  function handle3x3x3() {
      this.setState({
        my3x3x3: true
      })
      this.setState({
        oneHanded: false
      })
  }

  function oneHanded() {
    this.setState({
        my3x3x3: false
      })
      this.setState({
        oneHanded: true
      })
  }

  async function handleAddTime(evt) {
    await addTime(name,time);
    alert("Added!");
  }

  return <div>
    <h4 className="text">Enter your name, your time and then the cube you used to add it!</h4>
    <p><input type='text' value={name} onChange={handleUpdate} /></p>
    <p><input type='text' value={time} onChange={handleUpdateTime} /></p>

    <p></p>
    <button className='button-style' onClick={handleAddTime}>Add Time</button>


         <button className='button-style' onClick={showDropdownMenu}> Select Cube </button>

          { this.state.displayMenu ? (
          <ul>
         <li><button className='button-style' onClick={handle3x3x3}>3x3x3</button></li>
         <li><button className='button-style' onClick={oneHanded}>3x3x3 one-handed</button></li>

          </ul>
        ):
        (
          null
        )
        }

       </div>
}

export default App;
