import Layout from '../components/MyLayout.js';
import { getTime } from '../lib/utils';

export default class Times extends React.Component {
	state = {
		loading: true,
		person1: null,
		time1: 0,
		person2: null,
		time2: 0,
		person3: null,
		time3: 0,
		person4: null,
		time4: 0,
		person5: null,
		time5: 0,
	};

	async componentDidMount() {
		const url = "http://localhost:3001/list";
		const response = await fetch(url);
		const data = await response.json();
		this.setState({ person1: data.times[0].name, time1: data.times[0].time, loading: false });
		this.setState({ person2: data.times[1].name, time2: data.times[1].time, loading: false });
	}

	render() {
		return (
			<Layout>
				<div>
					{this.state.loading || !this.state.person ? (
						<div>There are not any times in the database. Record the first one!</div>
					) : (	
					 <div>
						<div>{this.state.person1}, {this.state.time1}</div>
						<div>{this.state.person2}, {this.state.time2}</div>
					 </div>
					)}
				</div>
			</Layout>
		)
	}


}
