import React from 'react';

class Home extends React.Component {
	constructor(props) {
		super(props);
		this.state = {secondsElapsed: 0};

	}

	// handleClick = () => {
	// 	alert(this.state.count);
	// }

	// getHours, getMinutes, getSeconds, getMilliseconds methods
	// handleStartClick, handleStopClick
	// getInitialState

	// getInitialState

	// <h1> {this.getSeconds()} </h1>
	// getSeconds() {
	// 	return ('0' + this.state.secondsElapsed % 60).slice(-2);
	// }

	// getInitialState() {
	// 	return {secondsElapsed: 0};
	// }

	// secondsElapsed * 100?
	getMilliseconds() {
		return null;
	}

	getSeconds() {
		return ('0' + this.state.secondsElapsed % 60).slice(-2);
	}

	getMinutes() {
		return Math.floor('0' + this.state.secondsElapsed / 60);
	}

	getHours() {
		return ((this.state.secondsElapsed / 60) / 60);
	}


	handleStartClick() {
		var _this = this;
		setInterval(() => {
			_this.setState({
				secondsElapsed: (_this.state.secondsElapsed + 1)
			});
		}, 1000)
	}

	handleStopClick() {
		alert("stopping...");
	}


	// align="center"

	render() {
		return (
			<div>
				<h1>{this.getHours()}:{this.getMinutes()}:{this.getSeconds()}</h1>
				<button type="button" onClick={this.handleStartClick}> start </button>
				<button type="button" onClick={this.handleStopClick}> stop </button>
			</div>
		);
	}

}

export default Home;
