import React from 'react';

class Home extends React.Component {

	// USE REFS, create ref setState milliSecondsElapsed: 0
	constructor(props) {
		super(props);
		this.state = {milliSecondsElapsed: 0};
		this.textInput = React.createRef();


		this.handleStopClick = this.handleStopClick.bind(this);
		this.keyPress = this.keyPress.bind(this);
		this.timer;

	}

	handleChange(e) {
		this.setState({milliSecondsElapsed: e.target.milliSecondsElapsed});
	}

	keyPress = (e) => {
		if (e.keyCode == 32){
			handleStopClick();

		}
	}

	getMilliseconds() {
		return ("0" + this.state.milliSecondsElapsed).slice(-2);
	}
	// var number = this.state.milliSecondsElapsed.toString().length;
	getSeconds() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		seconds = milli / 100;

		var myTrunc = Math.trunc(seconds);

		myTrunc = myTrunc % 60;

		return ("0" + myTrunc).slice(-2);
	}

	getMinutes() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		var minutes = 0;
		seconds = milli / 100;
		minutes = seconds / 60;

		var myTrunc = Math.trunc(minutes);

		return ("0" + myTrunc).slice(-2);

	}

	handleStartClick = () => {
		this.setState({ milliSecondsElapsed:
			this.textInput.current.milliSecondsElapsed
		});
		this.timer = setInterval(() => {
			this.setState({
				milliSecondsElapsed: (this.state.milliSecondsElapsed + 1)
			});
		}, 10)
	}

	handleStopClick = () => {
		clearInterval(this.timer);
	}

	// componentDidMount() {
	// 	let intervalId = setInterval(this.state.milliSecondsElapsed, 1000);
	// 	this.setState({intervalId: intervalId});
	// }

	// componentWillUnmount() {
	// 	clearInterval(this.state.intervalId);
	// }

	// <input value={this.state.milliSecondsElapsed} onKeyDown={this.keyPress} onChange={this.handleChange}/>

	render() {
		return (
			<div>
				<h1>{this.getMinutes()}:{this.getSeconds()}.{this.getMilliseconds()}</h1>
				<button type="button" onClick={this.handleStartClick}> start </button>
				<button type="button" onClick={this.handleStopClick}> stop </button>
				<input value={this.state.milliSecondsElapsed} onKeyDown={this.keyPress} ref={this.textInput}/>
			</div>
		);
	}

}

export default Home;
