import React, {useEffect, useReducer, useRef} from 'react';
import ReactDOM from 'react-dom';

class Home extends React.Component {

	// focus on start button before timer is started
	// once its started switch focus to stop button
	// redo

	constructor(props) {
		super(props);

		// create milliSeconds
		this.state = {milliSecondsElapsed: 0};
		this.updateState = this.updateState.bind(this);
		this.clearInput = this.clearInput.bind(this);
		// this.handleChange = this.handleChange.bind(this);
		// this.myRef = React.createRef();
		// this.focusRef = this.focusRef.bind(this);

	}

	updateState(e) {
		this.setState({milliSecondsElapsed: e.target.milliSecondsElapsed })
	}

	// when button is pressed will clear as 0, now need to add stopwatch and then make accesable by spacebar
	// clearInput() {
	// 	this.setState({milliSecondsElapsed: 0});
	// 	ReactDOM.findDOMNode(this.refs.myInput).focus();
	// }

	// update setState({milliSecondsElapsd: 0})

	// focusRef() {
	// 	this.myRef.current.focus();
	// }

	// handleChange(e) {
	// 	this.setState({milliSecondsElapsed: e.target.milliSecondsElapsed});
	// } 

	// handleSubmit = e => {
	// 	e.preventDefault();


	// }

	getMilliseconds() {
		return ("0" + this.state.milliSecondsElapsed).slice(-2);
	}


	// var number = this.state.milliSecondsElapsed.toString().length;
	getSeconds() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		seconds = milli / 100;

		var myTrunc = Math.trunc(seconds);

		myTrunc = myTrunc % 60;

		return ("0" + myTrunc).slice(-2);
	}


	getMinutes() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		var minutes = 0;
		seconds = milli / 100;
		minutes = seconds / 60;

		var myTrunc = Math.trunc(minutes);

		return ("0" + myTrunc).slice(-2);

	}

	handleStart = () => {
		this.setState({
			milliSecondsElapsed: (0)
		});
		this.timer = setInterval(() => {
			this.setState({
				milliSecondsElapsed: (this.state.milliSecondsElapsed + 1)
			});
		}, 10)
	}

	handleStop = () => {
		clearInterval(this.timer);
	}

	// <button onClick = {this.clearInput}>CLEAR</button>

	// <input value={this.state.milliSecondsElapsed} ref={this.myRef} onChange={this.handleChange} />
	render() {
		return (


			<div className="index">
				<input value = {this.state.milliSecondsElapsed} onChange = {this.updateState} ref="myInput"></input>
				<button onClick = {this.handleStart}>START</button>
				<button onClick = {this.handleStop}>STOP</button>
				<h4>{this.state.data}</h4>
			</div>

		);
	}

}

export default Home;
