import React, {useEffect, useReducer, useRef} from 'react';

class Home extends React.Component {

	// 

	constructor(props) {
		super(props);

		// create milliSeconds
		this.state = {milliSecondsElapsed: 0};
		this.handleChange = this.handleChange.bind(this);
		this.myRef = React.createRef();
		this.focusRef = this.focusRef.bind(this);


	}

	focusRef() {
		this.myRef.current.focus();
	}

	handleChange(e) {
		this.setState({milliSecondsElapsed: e.target.milliSecondsElapsed});
	} 


	render() {
		return (

			<input value={this.state.milliSecondsElapsed} ref={this.myRef} onChange={this.focusRef} />

		);
	}

}

export default Home;
