// App.js (client)
import React from 'react';
import { addTime } from "./util";
import Search from "../pages/search.js";


function App() {
  const [name, setName] = React.useState("")
  const [time, setTime] = React.useState("")
  const [cube, setCube] = React.useState("")
  const [click, setClick] = React.useState(false);
  const [rubiks, setRubiks] = React.useState(false);
  const [oh, setOh] = React.useState(false);
  const [my2x2x2, setMy2x2x2] = React.useState(false);
  const rx_live = /^[+-]?\d*(?:[.,]\d*)?$/;

  function handleUpdate(evt) {
    setName(evt.target.value);
  }

  function handleUpdateTime(evt) {
    if (rx_live.test(evt.target.value)) {
      setTime(evt.target.value);
    } else {
      alert("Please enter a number here.")
    }
  }

  function handleRubiks(evt) {
    setCube("3x3x3");
    setRubiks(true);
  }

  function handleOh(evt) {
    setCube("3x3x3 OH");
    setOh(true);
  }

  function handleMy2x2x2(evt) {
    setCube("2x2x2");
    setMy2x2x2(true);
  }

  function handleClick(evt) {
    setClick(true);
  }


  async function handleAddTime(evt) {
    await addTime(name,time);
    alert("Added!");
  }

  // <p><input type='text' value={cube} onChange={handleCube} /></p>
  return <div>
    <h4 className="text">Enter your name, your time and then the cube you used to add it!</h4>
    <p><input type='text' value={name} onChange={handleUpdate} /></p>
    <p><input type='text' value={time} onChange={handleUpdateTime} /></p>
    <p>{click}</p>
    {!click ? (
      <button className='button-style' onClick={handleClick}>Select Cube</button> 
    ) : rubiks ? (
      <h5 className='text'>3x3x3</h5>
    ) : oh ? ( 
      <h5 className='text'>3x3x3 OH</h5>
    ) : (  
      <div>
        <button className='button-style' onClick={handleRubiks}>3x3x3</button>
        <button className='button-style' onClick={handleOh}>3x3x3 OH</button>
        <button className='button-style' onClick={handleMy2x2x2}>2x2x2</button>
        <button className='button-style' onClick={handleOh}>3x3x3 OH</button>
        <button className='button-style' onClick={handleOh}>3x3x3 OH</button>
      </div>
    )}
    <p></p>
    <button className='button-style' onClick={handleAddTime}>Add Time</button>
  </div>
}

export default App;
