import React, {useEffect, useReducer, useRef} from 'react';
import ReactDOM from 'react-dom';

class Home extends React.Component {

	// focus on start button before timer is started
	// once its started switch focus to stop button
	// redo

	constructor(props) {
		super(props);
		this.state = {milliSecondsElapsed: 0};
		this.updateState = this.updateState.bind(this);
		this.textInput = React.createRef();
		this.state = { isStarted: false};

	}

	textInput = () => {
		clearInterval(this.timer);
	}

	onClickHandler() {
		this.setState(currentState => ({
			isStarted: !currentState.isStarted
		}));
	}

	updateState(e) {
		this.setState({milliSecondsElapsed: e.target.milliSecondsElapsed })
	}

	keyPress = (e) => {
		// g button
		if (e.keyCode == 103) {
			handleStart();
		}
	}

	keyPress = (e) => {
		// spacebar
		if (e.keyCode == 32) {
			handleStop();
		}
	}

	getMilliseconds() {
		return ("0" + this.state.milliSecondsElapsed).slice(-2);
	}

	getSeconds() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		seconds = milli / 100;
		var myTrunc = Math.trunc(seconds);
		myTrunc = myTrunc % 60;
		return ("0" + myTrunc).slice(-2);
	}

	getMinutes() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		var minutes = 0;
		seconds = milli / 100;
		minutes = seconds / 60;
		var myTrunc = Math.trunc(minutes);
		return ("0" + myTrunc).slice(-2);
	}

	handleStart = () => {
		this.setState({
			milliSecondsElapsed: (0)
		});
		this.timer = setInterval(() => {
			this.setState({
				milliSecondsElapsed: (this.state.milliSecondsElapsed + 1)
			});
		}, 10)
	}

	handleStop = () => {
		clearInterval(this.timer);
		this.textInput.current.focus();
	}

	// focus() {
	// 	this.textInput.current.focus();
	// }

	// componentDidMount() {
	// 	this.nameInput.focus();
	// }

	render() {
		return (


			<div className="index">
				<input value = {this.state.milliSecondsElapsed} onChange = {this.updateState} ref={this.textInput}></input>
				<button onClick = {this.handleStart}>START</button>
				<button onClick = {this.handleStop}>STOP</button>
			</div>

		);
	}

}

export default Home;
