import React from 'react';



class Menu extends React.Component {
constructor(){
 super();

 this.state = {
       displayMenu: false,
       my3x3x3: false,
       oneHanded: false,
     };

  this.showDropdownMenu = this.showDropdownMenu.bind(this);
  this.hideDropdownMenu = this.hideDropdownMenu.bind(this);
  this.handle3x3x3 = this.handle3x3x3.bind(this);
  this.oneHanded = this.oneHanded.bind(this);
};

showDropdownMenu(event) {
    event.preventDefault();
    this.setState({ displayMenu: true }, () => {
    	document.addEventListener('click', this.hideDropdownMenu);
    });
  }

  hideDropdownMenu() {
    this.setState({ displayMenu: false }, () => {
      document.removeEventListener('click', this.hideDropdownMenu);
    });
  }

  handle3x3x3() {
  	this.setState({
  		my3x3x3: true
  	})
  	// console.log(this.state.my3x3x3);
  }

  oneHanded() {
  	this.setState({
  		oneHanded: true
  	})
  }

  // async logCube() {
  // 	if (this.state.my3x3x3) {
  // 		await 
  // 	}
  // }

  render() {
    return (
        <div style = {{width:"200px"}} >
         <div onClick={this.showDropdownMenu}> Select </div>

          { this.state.displayMenu ? (
          <ul>
         <li><button onClick={this.handle3x3x3}>3x3x3</button></li>
         <li><button onClick={this.oneHanded}>3x3x3 one-handed</button></li>

          </ul>
        ):
        (
          null
        )
        }

       </div>

    );
  }
}

export default Menu;
