import React from 'react';

class Home extends React.Component {
	constructor(props) {
		super(props);
		this.state = {secondsElapsed: 0};
		this.handleStartClick = this.handleStartClick.bind(this);

	}

	// handleClick = () => {
	// 	alert(this.state.count);
	// }

	// getHours, getMinutes, getSeconds, getMilliseconds methods
	// handleStartClick, handleStopClick
	// getInitialState

	// getInitialState

	// <h1> {this.getSeconds()} </h1>
	// getSeconds() {
	// 	return ('0' + this.state.secondsElapsed % 60).slice(-2);
	// }

	// getInitialState() {
	// 	return {secondsElapsed: 0};
	// }

	// secondsElapsed * 100?
	getMilliseconds() {
		return null;
	}

	getSeconds() {
		return ('0' + this.state.secondsElapsed % 60).slice(-2);
	}

	// this isn't resetting at 0 after an hour, another '0' and slice?
	getMinutes() {
		return Math.floor(this.state.secondsElapsed / 60);
	}

	getHours() {
		return Math.floor((this.state.secondsElapsed / 60) / 60);
	}


	handleStartClick() {
		setInterval(() => {
			this.setState({
				secondsElapsed: (this.state.secondsElapsed + 1)
			});
		}, 1000)
	}

	handleStopClick() {
		clearInterval(this.incrementer);
	}


	// align="center"

	render() {
		return (
			<div>
				<h1>{this.getHours()}:{this.getMinutes()}:{this.getSeconds()}</h1>
				<button type="button" onClick={this.handleStartClick}> start </button>
				<button type="button" onClick={this.handleStopClick}> stop </button>
			</div>
		);
	}

}

export default Home;
