import React from 'react';

class Home extends React.Component {

	constructor(props) {
		super(props);
		this.state = {milliSecondsElapsed: 0};
		this.escFunction = this.escFunction.bind(this);

	}

	escFunction(event) {
		if (event.keyCode === 27) {
			console.log("yay!");
		}
	}

  	componentDidMount() {
    	document.addEventListener('keydown', this.escFunction, false);
  	}

    componentWillUnmount() {
    	document.removeEventListener('keydown', this.escFunction, true);
    }

	getMilliseconds() {
		return ("0" + this.state.milliSecondsElapsed).slice(-2);
	}
	// var number = this.state.milliSecondsElapsed.toString().length;
	getSeconds() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		seconds = milli / 100;

		var myTrunc = Math.trunc(seconds);

		myTrunc = myTrunc % 60;

		return ("0" + myTrunc).slice(-2);
	}

	getMinutes() {
		var milli = this.state.milliSecondsElapsed;
		var seconds = 0;
		var minutes = 0;
		seconds = milli / 100;
		minutes = seconds / 60;

		var myTrunc = Math.trunc(minutes);

		return (myTrunc);

	}

	handleStartClick = () => {
		this.timer = setInterval(() => {
			this.setState({
				milliSecondsElapsed: (this.state.milliSecondsElapsed + 1)
			});
		}, 10)
	}

	handleStopClick = () => {
		clearInterval(this.timer);
	}

	// <h1>{this.state.milliSecondsElapsed.toString().length}</h1>
	// <h1>{this.state.milliSecondsElapsed}</h1>

	render() {
		return (
			<div>
				<input/>
				<h1>{this.getMinutes()}:{this.getSeconds()}:{this.getMilliseconds()}</h1>
				<button type="button" onClick={this.handleStartClick}> start </button>
				<button type="button" onClick={this.handleStopClick}> stop </button>
			</div>
		);
	}

}

export default Home;
