import Layout from '../components/MyLayout.js';
//import { getTime } from '../lib/utils';
import Search from './search'; 
import App from "../lib/App.js";

/* <button className='button-style' onClick={this.handleRubiks}>Rubiks</button>
<h1 className='text'>Best Times</h1>
{this.state.loading || !this.state.people3x3 ? (
	<div className='text'>There are not any times in the database. Record the first one!</div>
) : this.state.myRubiks ? (
	<div className="text">{this.state.people3x3}</div>
) : (
	<p>uhoh</p>
)} */

export default class Times extends React.Component {
	state = {
		// names: [],
		// times: [],
		people: [],
		loading: true,
		click: false,
		myRubiks: false,
		my4x4: false,
	}

	async componentDidMount() {
		const url = "http://localhost:3001/api/list"; // const url = "http://35.194.72.130/api/list";
		const response = await fetch(url);
		const data = await response.json();
		// console.log(data.times.length);

		var newData = "";


		for (var i=0; i<data.times.length; i++) {
			newData = i+1 + ". " + data.times[i].name + ", " + data.times[i].time + ", " + data.times[i].cube;
			newData = newData.split('\n').map(str => <p>{str}</p>);


			if (data.times[i].cube === "3x3x3") {
				this.setState(previousState => ({
					people: [...previousState.people, newData]
				}));
				this.setState({
					loading: false
				})
			}

			if (data.times[i].cube === "4x4x4") {
				this.setState(previousState => ({
					people: [...previousState.people, newData]
				}));
				this.setState({
					loading: false
				})
			}
		}

	}

	handleRubiks = () => {
		this.setState({
			myRubiks: true
		})

	}

	handle4x4 = () => {
		this.setState({
			my4x4: true
		})
	}

	handleClick = () => {
		this.setState({
			click: true
		})
	}



	render() {
		return (
			<Layout>
				<div className='background' style={{height:575}}>
					<table align='left' width="50%">
						<App />
						<Search />
					</table>
					<table align="right" className="tableForTimes" width="50%">
						{!this.state.click ? (
							<button className='button-style' onClick={this.handleClick}>Select Cube</button>
						) : this.state.myRubiks ? (
							<div className="text">{this.state.people}</div>
						) : this.state.my4x4 ? (
							<div className="text">{this.state.people}</div>
						) : (
							<button className='button-style' onClick={this.handleRubiks}>3x3x3</button>
						)}




					</table>
				</div>
			</Layout>
		)
	}
}
