import React from "react";
import { getInfo } from "../lib/utilSearch";

class Search extends React.Component {
	constructor(props) {
		super(props);
		this.state = {search: ""};
		this.state = {length: 0};
	}

	handleUpdate(evt) {
    	this.setState({search: evt.target.value});
  	}

  	async handleSearch(evt) {
  		const user = await getInfo(this.state.search);
  		console.log(user);
  		this.setState({user: user.rank[0]})
  	}

  	async componentDidMount() {
  		const url = "http://localhost:3001/api/length";
  		const response = await fetch(url);
		const data = await response.json();

		try {
			this.setState({ length: data.rows})
		} catch (err) {

		}
  	}

  	render() {
  		return (
  			<div>
  			<h3 className="text">Enter a name below to search for it and see your rank!</h3>
  			<p><input type='text' value={this.state.search} onChange={this.handleUpdate.bind(this)} /></p>
  			<button className="button-style" onClick={this.handleSearch.bind(this)}>Search</button>

  			{this.state.user ? <div>
  				<h3 className='text'> {this.state.user.name}, {this.state.user.time}, {this.state.user.rank} </h3>
  				<h3> There are {this.state.length} people in the database! </h3>
  			</div> : null}

  			</div>
  		)
  	}
}

export default Search;
